mapper.select:
SELECT
    pl.*,
    u.user_name AS userName,
    s.NAME AS supplierName
FROM
    t_purchase_list pl
    LEFT JOIN t_user u ON pl.user_id = u.id
    LEFT JOIN t_supplier s ON pl.supplier_id = s.id


         pl.purchase_number =#{purchaseListQuery.purchaseNumber}


         and pl.supplier_id=#{purchaseListQuery.supplierId}


         and pl.state = #{purchaseListQuery.state}


         and purchase_date >=#{purchaseListQuery.startDate}


        and purchase_date <=#{purchaseListQuery.endDate}

select
  count(*)
from t_purchase_list pl
   LEFT JOIN t_supplier s on pl.supplier_id = s.id
     left join t_purchase_list_goods plg on pl.id = plg.purchase_list_id
     LEFT JOIN t_goods_type gt on plg.type_id = gt.id


        and purchase_date >= #{purchaseListQuery.startDate}


        and purchase_date <= #{purchaseListQuery.endDate}


        and
        (
        plg.name like concat('%',#{purchaseListQuery.goodsName},'%') or plg.code =#{purchaseListQuery.goodsName}
        )


        and plg.type_id in

            #{typeId}

select pl.purchase_number as number, pl.purchase_date as date, s.`name` as name, plg.`code`, plg.`name` as goodsName, plg.model, plg.unit as unitName, plg.price, plg.num, plg.total, gt.`name` as typeName from t_purchase_list pl LEFT JOIN t_supplier s on pl.supplier_id = s.id left join t_purchase_list_goods plg on pl.id = plg.purchase_list_id LEFT JOIN t_goods_type gt on plg.type_id = gt.id
and purchase_date >=#{purchaseListQuery.startDate}
and purchase_date <=#{purchaseListQuery.endDate}
and ( plg.name like concat('%',#{purchaseListQuery.goodsName},'%') or plg.code =#{purchaseListQuery.goodsName} )
and plg.type_id in
#{typeId}
limit #{purchaseListQuery.index},#{purchaseListQuery.limit}
