mapper.select:
SELECT
sl.*,
u.user_name AS userName,
c.NAME AS customerName
FROM
t_sale_list sl
LEFT JOIN t_user u ON sl.user_id = u.id
LEFT JOIN t_customer c ON sl.customer_id = c.id


        sl.sale_number =#{saleListQuery.saleNumber}


        and sl.customer_id=#{saleListQuery.customerId}


        and sl.state = #{saleListQuery.state}


        and sale_date >= #{saleListQuery.startDate}


        and sale_date <= #{saleListQuery.endDate}

select
count(*)
from t_sale_list sl
LEFT JOIN t_customer c on sl.customer_id = c.id
left join t_sale_list_goods plg on sl.id = plg.sale_list_id
LEFT JOIN t_goods_type gt on plg.type_id = gt.id


        and sale_date >=#{saleListQuery.startDate}


        and sale_date <=#{saleListQuery.endDate}


        and
        (
        plg.name like concat('%',#{saleListQuery.goodsName},'%') or plg.code =#{saleListQuery.goodsName}
        )


        and plg.type_id in

            #{typeId}

select sl.sale_number as number, sl.sale_date as date, c.`name` as name, plg.`code`, plg.`name` as goodsName, plg.model, plg.unit as unitName, plg.price, plg.num, plg.total, gt.`name` as typeName from t_sale_list sl LEFT JOIN t_customer c on sl.customer_id = c.id left join t_sale_list_goods plg on sl.id = plg.sale_list_id LEFT JOIN t_goods_type gt on plg.type_id = gt.id
and sale_date >=#{saleListQuery.startDate}
and sale_date <=#{saleListQuery.endDate}
and ( plg.name like concat('%',#{saleListQuery.goodsName},'%') or plg.code =#{saleListQuery.goodsName} )
and plg.type_id in
#{typeId}
limit #{saleListQuery.index},#{saleListQuery.limit}
